commits.content:
* {
    box-sizing: border-box;
    margin:0;
    padding: 0;
  }

  html,
  body {
    height: 100%;
    padding: 0px;
    margin: 0px;
    font-family: serif;

  }
  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    font-family: serif;
    color: #5c5b5b;
    margin: 5px;
  }

  a {
    text-decoration: none;
    color: inherit;
  }
  .pagination{
    width: 20%;
    margin: 10px auto;
  }
  .page{
    display: inline-block;
    width: 40px;
    height: 40px;
    text-align: center;
    font-size: x-large;
    padding-top: 5px;
    background: #ccc;
    border:1px solid black;
    border-radius: 5px;
  }
  .page.active{
    background:  #0b6c99;
  }

  .btn {
    padding: .5rem 1rem;
    background: #0dbe2a;
    color: white;
    border: 1px solid transparent;
    border-radius: .25rem;
    font-size: 1.08em;
    font-family: 'Lora', serif;
  }

  .btn-big {
    padding: .7rem 1.3rem;
    line-height: 1.3rem;
  }

  .btn:hover {
    color: white !important;
    background: #0a8d75;
  }

  .text-input {
    padding: .7rem 1rem;
    display: block;
    width: 100%;
    border-radius: 5px;
    border: 1px solid #e0e0e0;
    outline: none;
    color: #444;
    line-height: 1.5rem;
    font-size: 1.2em;
    font-family: 'Lora', serif;
  }

  .msg {
    width: 100%;
    margin: 5px auto;
    padding: 8px;
    border-radius: 5px;
    list-style: none;
  }

  .success {
    color: #3a6e3a;
    border: 1px solid #3a6e3a;
    background: #bcf5bc;
  }

  .error {
    color: #884b4b;
    border: 1px solid #884b4b;
    background: #f5bcbc;
  }

  /* NAVBAR */

  header{
    background-color:black;
    height: 66px;
    margin:0px;
  }

  header * {
    color: white;
  }

  header .logo {
    float: left;
    height: inherit;
    margin-left: 2em;
  }

  header .logo-text {
    margin: 9px;
    font-family: 'Candal', serif;
  }

  header .logo-text span {
    color: #05f7ff;
  }

  header ul {
    float: right;
    margin: 0px;
    padding: 0px;
    list-style: none;
  }

  header ul li {
    float: left;
    position: relative;

  }

  header ul li ul {
    position: absolute;
    top: 66px;
    right: 0px;
    width: 180px;
    display: none;
    z-index: 88888;
  }



  header ul li ul li {
    width: 100%;
  }

  header ul li ul li a {
    padding: 10px;
    background: white;

  }

  header ul li a {
    display: block;
    padding: 21px;
    font-size: 1.1em;
    text-decoration: none;
  }





  .page-container {
    min-height: 90%;
  }

  .page-container a:hover {
    color: #006669;
  }


  .posts .posts-title {
    text-align: center;
    margin: 30px auto;
  }
  .posts .post-container {
    width: 84%;
    height: 350px;
    margin: 0px auto;
    overflow: hidden;
    padding: 10px 0px 10px 0px;
  }

  .posts .post-container .post {
    width: 300px;
    height: 330px;
    margin: 0px 10px;
    display: inline-block;
    background: white;
    border-radius: 5px;
    box-shadow: 1rem 1rem 1rem -1rem #a0a0a033;
  }

  .posts .post-container .post .post-info {
    height: 130px;
    padding: 0px 5px;
  }

  .posts .post-container .post .slider-image {
    width: 100%;
    height: 200px;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
  }

  /* CONTENT */

  .container {
    width: 90%;
    margin: auto;
  }

  .container .main-content {

    margin: auto;
  }

  .container .main-content.single {
    background: white;
    padding: 20px 50px;
    font-size: 1.1em;
    border-radius: 5px;
  }

  .container .main-content.single .post-title {
    text-align: center;
    margin-bottom: 40px;
  }
  .container .main-content.single .post-image{
    margin:auto;
    height: 400px;
    width: 100%;
  }
  .container .main-content.single .post-image img{
    height: 100%;
    width: 100%;

  }
  .comments{
    background: #fff;
    padding: 20px;
  }
  .comment .contenu{

    font-size: large;
    overflow:hidden;
  }
  .comment .sup{
    color: #e91515;
  }

  /* FOOTER */

  .footer {
    background: #303036;
    color: #d3d3d3;
    height: 400px;
    position: relative;
  }

  .footer .footer-content {
    height: 350px;
    display: flex;
  }

  .footer .footer-content .footer-section {
    flex: 1;
    padding: 25px;
  }

  .footer .footer-content h1,
  .footer .footer-content h2 {
    color: white;
  }

  .footer .footer-content .about h1 span {
    color: #05f7ff;
  }

  .footer .footer-content .about .contact span {
    display: block;
    font-size: 1.1em;
    margin-bottom: 8px;
  }

  .footer .footer-content .about .socials a {
    border: 1px solid gray;
    width: 45px;
    height: 41px;
    padding-top: 5px;
    margin-right: 5px;
    text-align: center;
    display: inline-block;
    font-size: 1.3em;
    border-radius: 5px;
    transition: all .3s;
  }

  .footer .footer-content .about .socials a:hover {
    border: 1px solid white;
    color: white;
    transition: all .3s;
  }

  .footer .footer-content .links ul a {
    display: block;
    margin-bottom: 10px;
    font-size: 1.2em;
    transition: all .3s;
  }

  .footer .footer-content .links ul a:hover {
    color: white;
    margin-left: 15px;
    transition: all .3s;
  }

  .footer .footer-content .contact-form .contact-input {
    background: #272727;
    color: #bebdbd;
    margin-bottom: 10px;
    line-height: 1.5rem;
    padding: .9rem 1.4rem;
    border: none;
  }

  .footer .footer-content .contact-form .contact-input:focus {
    background: #1a1a1a;
  }

  .footer .footer-content .contact-form .contact-btn {
    float: right;
    font-size: 1.1em;
    font-family: 'Lora', serif;
  }

  .footer .footer-bottom {
    background: #343a40;
    color: #686868;
    height: 50px;
    width: 100%;
    text-align: center;
    position: absolute;
    bottom: 0px;
    left: 0px;
    padding-top: 20px;
  }
  admin-container {
    height: auto;
  }

  .admin-container .admin-content {
    height: 100%;
    padding: 40px;
  }
  .admin-content .page-title {
    text-align: center;
    margin-bottom: 1rem;
  }

  table {
    width: 100%;
    border-collapse: collapse;
    font-size: 1.1rem;
  }

  th,
  td {
    padding: 15px;
    text-align: left;
    border-bottom: 1px solid #d3d3d3;
  }

  .edit {
    color: green;
  }

  .delete {
    color: red;
  }


  .edit:hover,
  .delete:hover {
    text-decoration: underline;
  }

  form div {
    margin-bottom: 15px;
  }
